INSERT INTO `wp_posts` VALUES (588,1,'2023-08-10 08:50:16','2023-08-10 08:50:16','<!-- wp:cover {\"useFeaturedImage\":true,\"minHeight\":150,\"minHeightUnit\":\"px\",\"customGradient\":\"linear-gradient(135deg,rgb(7,0,38) 0%,rgb(116,224,176) 100%)\",\"contentPosition\":\"center center\",\"isDark\":false,\"align\":\"full\",\"style\":{\"color\":{\"duotone\":[\"#000000\",\"#7f7f7f\"]},\"spacing\":{\"padding\":{\"top\":\"0\",\"right\":\"0\",\"bottom\":\"0\",\"left\":\"0\"}}},\"fontSize\":\"small\"} -->\n<div class=\"wp-block-cover alignfull is-light has-small-font-size\" style=\"padding-top:0;padding-right:0;padding-bottom:0;padding-left:0;min-height:150px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim-100 has-background-dim has-background-gradient\" style=\"background:linear-gradient(135deg,rgb(7,0,38) 0%,rgb(116,224,176) 100%)\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:group {\"style\":{\"spacing\":{\"padding\":{\"top\":\"12%\",\"bottom\":\"18%\"}}},\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\" style=\"padding-top:12%;padding-bottom:18%\"><!-- wp:heading {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0%\",\"bottom\":\"0%\"}},\"typography\":{\"textDecoration\":\"underline\"}}} -->\n<h2 class=\"wp-block-heading\" style=\"margin-top:0%;margin-bottom:0%;text-decoration:underline\">My Projects</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"19px\"},\"spacing\":{\"margin\":{\"top\":\"50px\"}}}} -->\n<p style=\"margin-top:50px;font-size:19px\">Hey , thanks for checking out this page containing some of my most documented projects! Feel free to contact me with any questions you might have.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:group {\"layout\":{\"inherit\":true,\"type\":\"constrained\",\"contentSize\":\"450px\"}} -->\n<div class=\"wp-block-group\"><!-- wp:sp-wp-carousel-pro/shortcode {\"shortcode\":\"412\"} /--></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Snake / Python 3.x + Tkinter -&gt; <span style=\"text-decoration: underline;\"><a rel=\"noreferrer noopener\" href=\"https://github.com/tobyhogan/retro-snake-game\" data-type=\"URL\" data-id=\"https://github.com/tobyhogan/retro-snake-game\" target=\"_blank\">Github</a></span></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Trying to work out how to implement a system where the tail of the snake follow the rest of the snake properly and ensure that the visual assets match what the snake is doing.</em><br><span style=\"text-decoration: underline;\">Most Interesting part</span>: L<em>earning how to make the menus work properly - for instance the start menu(with the play/quit buttons) and ensuring that it seems responsive to the end user.</em><br><span style=\"text-decoration: underline;\">Key Learning:</span> <em>How the core system behind games can work including the refresh or tick rate, and managing the presence of assets and elements on the main window. </em></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:group {\"layout\":{\"inherit\":true,\"type\":\"constrained\",\"contentSize\":\"500px\"}} -->\n<div class=\"wp-block-group\"><!-- wp:sp-wp-carousel-pro/shortcode {\"shortcode\":\"436\"} /--></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Two Player Dice Game / Python 3.x + Tkinter -> [github]]]</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Trying to figure out how to set up the online version of the game using servers, ports - ensuring that all the timing and data transfer </em>works properly.<br><span style=\"text-decoration: underline;\">Most Interesting part</span>: <em>Implementing a global settings menu that changes features across the entirity of the program - including things like colour themes and the music/volume</em>.<br><span style=\"text-decoration: underline;\">Key Learning:</span> <em>Important ideas in how complex and large applications can be structured efficiently, especially considering that I built three full versions of this application all using different paradigms or organisational methods.</em></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:group {\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"center\",\"id\":488,\"width\":391,\"height\":321,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-large is-resized\"><img src=\"http://localhost:10006/wp-content/uploads/2023/08/Screenshot-2023-06-23-at-01.15.48-edited.png\" alt=\"\" class=\"wp-image-488\" width=\"391\" height=\"321\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Studying Timer Site / Raw HTML + CSS + JavaScript</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Working with all the complex functions involved in the timing system and ensuring that the different parts of the application can communicate with eachother</em> properly and without bugs.<br><span style=\"text-decoration: underline;\">Most Interesting part</span>: <em>Building out different iterations of the website using different ideas that could be used to optimise the user\'s time effectively and then building out the site using that idea.</em><br><span style=\"text-decoration: underline;\">Key Learning:</span> <em>Gaining a more intuitive understanding of some asynchronous and promise related behaviour in JavaScript;</em></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:sp-wp-carousel-pro/shortcode {\"shortcode\":\"446\"} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Personal Website - Previous Version / CSS, HTML &amp; JS</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Ensuring that the mobile version of the site functioned as well as the desktop version and problem-solving in what way I should make the elements appear.</em><br><span style=\"text-decoration: underline;\">Most Interesting part</span>: <em>Learning about different methods of implementing functionality that I wanted from the website; e.g. contact forms and downloadable files using webhosting</em>.<br><span style=\"text-decoration: underline;\">Key Learning:</span> How to effectively implement web-hosting solutions for static sites, ensuring that all parts of the site work for users.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:group {\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"center\",\"id\":516,\"width\":447,\"height\":332,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-large is-resized\"><img src=\"http://localhost:10006/wp-content/uploads/2023/08/Screenshot-2023-08-01-at-15.21.14-1-1024x760.png\" alt=\"\" class=\"wp-image-516\" width=\"447\" height=\"332\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Biographical Link Sharing Website / React.js, Express.js, Node.js, Firebase</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Implementing a system of storing user information and cookies so that users sessions would be preserved and they would have access to certain features within their account.</em><br><span style=\"text-decoration: underline;\">Most Interesting part</span>: <em>Building my knowledge on how to implement scalable and maintainable backend data-storage </em>solutions.<br><span style=\"text-decoration: underline;\">Key Learning:</span> <em>Increasing my confidence in areas of development in React, React Router and Express </em></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:group {\"align\":\"wide\",\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:group {\"layout\":{\"inherit\":true,\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"center\",\"id\":518,\"width\":483,\"height\":262,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"http://localhost:10006/wp-content/uploads/2023/08/Screenshot-2023-08-09-at-19.00.27.png\" alt=\"\" class=\"wp-image-518\" width=\"483\" height=\"262\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"><!-- wp:heading {\"level\":3,\"fontSize\":\"large\"} -->\n<h3 class=\"wp-block-heading has-large-font-size\">Meditation Timer / React.js, Express.js, Node.js</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"placeholder\":\"Content\"} -->\n<p><span style=\"text-decoration: underline;\">Hardest part</span>: <em>Implementing some features of the user progress tracking system and storing user information effectively</em>.<br><span style=\"text-decoration: underline;\">Most Interesting part</span>: <em>Creating the different animations and effects for the instructions of the timer to the user, ensuring that potential users would be able to understand them.</em><br><span style=\"text-decoration: underline;\">Key Learning:</span> Optimising the UX(user experience), so that they could have a clear and effective workflow when using the application.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->','Projects','','inherit','closed','closed','','36-revision-v1','','','2023-08-10 08:50:16','2023-08-10 08:50:16','',36,'http://localhost:10006/?p=588',0,'revision','',0);
